// +build ignore

package main

import (
	"fmt"
	"log"
	"os"
	"strings"
	"time"
)

var (
	PipeXCount = 16
)

func die(err error) {
	if err != nil {
		log.Fatal(err)
	}
}

func main() {
	f, err := os.Create("stream_dupn.go")
	die(err)
	defer f.Close()

	fmt.Fprintf(f, `// Code generated by go generate; DO NOT EDIT.
// This file was generated by stream_dupn_gen.go at %s
package flow

`, time.Now().UTC())

	for i := 2; i <= PipeXCount; i++ {
		fmt.Fprintf(f, "func (s *Stream) Dup%d(chBuf int) (", i)

		{
			var outs []string
			for j := 0; j < i; j++ {
				outs = append(outs, "*Stream")
			}
			fmt.Fprintf(f, strings.Join(outs, ", "))
		}

		fmt.Fprintf(f, `) {
	outs := s.DupN(chBuf, %d)
	return `, i)
		{
			var outs []string
			for j := 0; j < i; j++ {
				outs = append(outs, fmt.Sprintf("outs[%d]", j))
			}
			fmt.Fprintf(f, strings.Join(outs, ", "))
		}
		fmt.Fprintf(f, `
}

`)

	}

}
